import React, { Component } from "react";
import { axios, swal } from "../dependencies";
import roleChecker from "./Routes/roleChecker";
import NavBar from "./NavBar/NavBar";

class StudentMenu extends Component {
	state = {
		courses: [],
		tMachines: [],
		loggedId: "",
	};

	componentDidMount = () => {
		this.setLoggedId();
		this.getTMachines();
	};

	setLoggedId() {
		this.state.loggedId = roleChecker.getLoggedId();
		if (this.state.loggedId === undefined) {
			swal.fire({
				title: "Oops !",
				text: "User does not have access to this page. Please login to access.",
				icon: "error",
				background: "black",
				color: "white",
			}).then(() => {
				window.location = "/login";
			});
		}
	}

	getCourses = () => {
		let apiUrl = "/api/students/" + this.state.loggedId + "/courses";
		axios({
			url: apiUrl,
			method: "GET",
		})
			.then((res) => {
				const data = res.data.data;
				const courses = [];
				data.forEach((element) => {
					const course = {
						code: element.code,
						name: element.name,
					};
					courses.push(course);
				});
				this.setState({
					courses: courses,
				});
				console.log(this.state.courses);
				console.log("Data courses lista");
			})
			.catch((err) => {
				console.log(err);
				swal.fire({
					title: "Error!",
					text: err,
					icon: "warning",
					background: "black",
					color: "white",
				});
			});
	};

	getTMachines = () => {
		const apiUrl = "/api/students/" + this.state.loggedId + "/tmachines";
		axios({
			url: apiUrl,
			method: "GET",
		})
			.then((res) => {
				const data = res.data.data;
				const tMachines = [];
				data.forEach((element) => {
					const tMachine = {
						id: element.id,
						description: element.description,
					};
					tMachines.push(tMachine);
				});
				this.setState({
					tMachines: tMachines,
				});
				console.log(this.state.tMachines);
				console.log("Data lista");
			})
			.catch((err) => {
				console.log(err);
				swal.fire({
					title: "Error!",
					text: "Unexpected error, Try Again",
					icon: "warning",
					background: "black",
					color: "white",
				});
			});
	};

	displayTMachines = (tMachines) => {
		if (tMachines.length === 0) {
			return (
				// eslint-disable-next-line jsx-a11y/anchor-is-valid
				<a class="list-group-item list-group-item-action disabled">
					No Turing Machines registered
				</a>
			);
		}

		return tMachines.map((tMachine) => (
			<a
				href={"/students/simulator/" + tMachine.id}
				class="list-group-item list-group-item-action"
				aria-current="true"
			>
				{tMachine.id} - {tMachine.description}
			</a>
		));
	};

	displayCourses = (courses) => {
		if (courses.length === 0) {
			return (
				// eslint-disable-next-line jsx-a11y/anchor-is-valid
				<a class="list-group-item list-group-item-action disabled">
					No Courses enrolled
				</a>
			);
		}

		return courses.map((course) => (
			<a
				href={"/students/courses/" + course.code}
				class="list-group-item list-group-item-action"
				aria-current="true"
			>
				{course.code} - {course.name}
			</a>
		));
	};

	render() {
		return (
			<div class="StudentMenu">
				<NavBar />
				<div id="container">
					<h1 id="title">Student Menu</h1>
					<div class="row">
						<div class="col-6">
							<div class="accordion" id="courses-accordion">
								<div class="accordion-item">
									<h2
										class="accordion-header"
										id="courses-heading"
									>
										<button
											type="button"
											class="accordion-button collapsed"
											data-bs-toggle="collapse"
											data-bs-target="#courses-data"
										>
											Courses
										</button>
									</h2>
									<div
										id="courses-data"
										class="accordion-collapse collapse"
										aria-labelledby="courses-heading"
										data-bs-parent="#courses-accordion"
									>
										<div class="list-group">
											{this.displayCourses(
												this.state.courses
											)}
										</div>
									</div>
								</div>
							</div>
						</div>
						<div class="col-6">
							<div class="accordion" id="tmachines-accordion">
								<div class="accordion-item">
									<h2
										class="accordion-header"
										id="tmachines-heading"
									>
										<button
											type="button"
											class="accordion-button collapsed"
											data-bs-toggle="collapse"
											data-bs-target="#tmachines-data"
										>
											Turing Machines
										</button>
									</h2>
									<div
										id="tmachines-data"
										class="accordion-collapse collapse"
										aria-labelledby="tmachines-heading"
										data-bs-parent="#tmachines-accordion"
									>
										<div class="list-group">
											<a
												href="/students/tmachines/"
												class="list-group-item list-group-item-action"
												aria-current="true"
											>
												Create New Turing Machine
											</a>
											{this.displayTMachines(
												this.state.tMachines
											)}
										</div>
									</div>
								</div>
							</div>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

export default StudentMenu;
